/**购物车条目**/
CREATE TABLE lk_pdd_cart(
  goods_id INT PRIMARY KEY AUTO_INCREMENT,
  goods_name VARCHAR(255),      
  thumb_url VARCHAR(255),  
  price VARCHAR(255),       
  buy_count VARCHAR(255),
  is_pay BOOLEAN
);




CREATE TABLE pdd_recommend(
  goods_id      INT,
  goods_name    VARCHAR(255),
  short_name    VARCHAR(255),
  thumb_url     VARCHAR(255),
  hd_thumb_url    VARCHAR(255),
  img_url    VARCHAR(255),
  price    VARCHAR(255),
  normal_price   VARCHAR(255),
  market_price   VARCHAR(255),
  sales_tip   VARCHAR(255),
  hd_url   VARCHAR(255)
);









